import { theme, ThemeProvider } from "./theme";
import Header from "./components/header/Header";

import "./App.css";
import NavButtonGroup from "./components/buttonGroup/NavButtonGroup";
import { Route, Routes, useLocation } from "react-router-dom";
import Home from "./pages/home/Home";
import { Products } from "../src/data/products";
import Produtos from "./pages/produtos/Produtos";
import Servicos from "./pages/servicos/Servicos";
import Sobre from "./pages/sobre/Sobre";
import Contato from "./pages/contato/Contato";
import Footer from "./components/footer/Footer";
import Duvidas from "./pages/duvidas/Duvidas";
import Customer from "./pages/cadastro-cliente/Customer";
import MinhaConta from "./pages/minha-conta/MinhaConta";
import Login from "./pages/login/Login";
import React, { useState } from "react";
/* import Login from './pages/login/Login'; */
import ClientCheckout from "./pages/client-Checkout/ClientCheckout";

interface ProductType {
  id: number;
  title: string;
  price: number;
  description: string;
  category: string;
  image: string[];
  quantity?: number;
  rating?: { rate: number; count: number };
}

function App() {
  const location = useLocation();
  const path = location.pathname;

  // Estado do carrinho
  const [cart, setCart] = useState<ProductType[]>([]);

  // Função para continuar comprando
  const handleContinueShopping = () => {
    // Implemente a lógica para continuar comprando
  };

  // Inicializa o estado 'expanded' com base na rota atual
  const getInitialExpandedPanel = () => {
    switch (path) {
      case "/central-atendimento":
        return "formasPagamento"; // Ou qualquer identificador que você preferir
      case "/ajuda-suporte":
        return "politicaPrivacidade"; // Ou outro identificador
      // Adicione mais casos conforme necessário
      default:
        return false;
    }
  };

  /*   const [expanded, setExpanded] = React.useState<string | false>(
    getInitialExpandedPanel() // Chame a função sem passar 'path'
  ); */

  const [expanded, setExpanded] = useState<string | false>(false);

  // Lógica para adicionar um item ao carrinho

  React.useEffect(() => {
    let newExpandedState: string | false = false; // Inicializa com falso por padrão
    switch (path) {
      case "/ajuda-suporte":
        newExpandedState = "formasPagamento";
        break;
      case "/ajuda-suporte/politica-privacidade":
        newExpandedState = "politicaPrivacidade";
        break;
      case "/ajuda-suporte/politica-reembolso":
        newExpandedState = "politicaReembolso";
        break;
      case "/ajuda-suporte/trocas-devolucoes":
        newExpandedState = "trocaDevolucoes";
        break;
      case "/ajuda-suporte/prazo-entrega":
        newExpandedState = "prazoEntrega";
        break;
      case "/ajuda-suporte/compra-segura":
        newExpandedState = "compraSegura";
        break;
      default:
        newExpandedState = false;
        break;
    }
    setExpanded(newExpandedState);
  }, [path]);

  return (
    <ThemeProvider theme={theme}>
      <div className="app-container">
        <header>
          <Header />
        </header>
        <nav>
          <NavButtonGroup />
        </nav>

        <main>
          <Routes>
            <Route path="/" element={<Home />} />
            {/* <Route path='/minha-conta' element={<MinhaConta />} /> */}
            <Route path="/sobre" element={<Sobre />} />
            <Route path="/servicos" element={<Servicos />} />
            <Route path="/contato" element={<Contato />} />
            {/*  <Route path='/ajuda-suporte' element={<AjudaSuporte />} /> */}
            <Route
              path="/duvidas"
              element={
                <Duvidas expanded={expanded} setExpanded={setExpanded} />
              }
            />

              }
            />
          </Routes>
        </main>
        <Footer />
        {/* Botão do WhatsApp fixo */}
        <div className="whatsapp-button">
          <a
            href="https://wa.me/5521965502519"
            target="_blank"
            rel="noopener noreferrer"
          >
            <img
              src="https://www.drpetdomiciliar.com.br/images/ico_whatsapp_float.png"
              alt="WhatsApp"
              className="whatsapp-icon"
            />
          </a>
        </div>
      </div>
    </ThemeProvider>
  );
}

export default App;
