import React, { useEffect, useState } from "react";
import Logo from "../../assets/logo.png";
import HeaderButtonGroup from "../buttonGroup/HeaderButtonGroup";
import CartComponent from "../cart/Cart";
import "./Header.css";
import { RootReducer } from "../../redux/root-reducer";
import { useSelector } from "react-redux";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import Badge from "@mui/material/Badge";
import { Link } from "react-router-dom";

function Header() {
  const { cart } = useSelector(
    (rootReducer: RootReducer) => rootReducer.cartReducer
  );

  const [showCart, setShowCart] = useState(false);
  const [itemCount, setItemCount] = useState(0);

  const calculateItemCount = () => {
    const count = cart.reduce((total, product) => total + (product.quantity ?? 0), 0);
    console.log("Número de itens calculado:", count);
    return count;
  };

  useEffect(() => {
    // Atualize a contagem de itens no carrinho sempre que o carrinho for atualizado
    setItemCount(calculateItemCount());
  }, [cart]);

  const toggleCart = () => {
    setShowCart(!showCart);
  };

  const handleContinueShopping = () => {
    setShowCart(false); // Oculta o carrinho ao clicar em "Continuar comprando"
  };

  return (
    <div className="header-container">
      <div className="logo">
        <img className="header-logo" src={Logo} alt="Logo" />
      </div>

      <div className="cart" onClick={toggleCart}>
        <Badge badgeContent={itemCount} color="error" className="header-cart">
          <ShoppingCartIcon sx={{ fontSize: 40 }} />
        </Badge>
      </div>
      <HeaderButtonGroup />
      <hr />

      {showCart && <CartComponent showCart={showCart} cart={cart} />}

      {/* Adicione um link para continuar comprando */}
      <Link to="/produtos" onClick={handleContinueShopping}>Continuar comprando</Link>
    </div>
  );
}

export default Header;

