interface ProductCardProps {
  product: Product; // Corrigindo o tipo para Product
}

const ProductCard: React.FC<ProductCardProps> = ({ product }) => {
  const { cart } = useSelector((state: RootState) => state.cart);
  const dispatch = useDispatch();

  // Verificando se o produto está no carrinho
  const isProductOnCart = cart.some((item) => item.id === product.id);

  // Função para adicionar ou remover o produto do carrinho
  const handleCartAction = () => {
    if (isProductOnCart) {
      dispatch(removeProduct(product));
    } else {
      dispatch(addProduct(product));
    }
  };

  return (
    <div className="card">
      <img className="product_image" src={product.image} alt={product.title} />
      <h2 className="product_title">{product.title}</h2>
      <div className="card_container">
        <span className="card_review">
          {Array.from({ length: 5 }).map((_, index) =>
            index < Math.round(product.rating.rate) ? (
              <AiFillStar className="card_svg" key={index} />
            ) : (
              <AiOutlineStar className="card_svg" key={index} />
            )
          )}
          ({product.rating.rate})
        </span>
        <strong>R${product.price}</strong>
      </div>
      <button
        className={isProductOnCart ? "card_removeFromCart" : "card_addToCart"}
        onClick={handleCartAction}
      >
        {isProductOnCart ? "Remover do carrinho" : "Adicionar ao carrinho"}
        <FiShoppingCart className="card_buttonSVG" />
      </button>
    </div>
  );
};

export default ProductCard;
