import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { Products } from "../../data/products";
import { removeProduct } from "../../redux/Cart/cart-slice";
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import AddCircleSharpIcon from '@mui/icons-material/AddCircleSharp';
import RemoveCircleSharpIcon from '@mui/icons-material/RemoveCircleSharp';
import './Cart.css';

interface CartProps {
  showCart: boolean;
  cart: Products[];
  onContinueShopping: () => void; 
}

const CartComponent: React.FC<CartProps> = ({ showCart, cart, onContinueShopping }) => {
  const dispatch = useDispatch();
  const [expandedProduct, setExpandedProduct] = useState<Products | null>(null); // Estado para controlar o produto expandido

  const handleToggleExpand = (product: Products) => {
    if (expandedProduct && expandedProduct.id === product.id) {
      // Se o produto atualmente expandido é clicado novamente, feche-o
      setExpandedProduct(null);
    } else {
      // Caso contrário, expanda o produto clicado
      setExpandedProduct(product);
    }
  };

  const handleSendWhatsAppMessage = () => {
    // Lógica para criar a mensagem do WhatsApp com os produtos do carrinho e abrir o aplicativo
    let message = `Olá! Estou interessado nos seguintes produtos:\n`;

    cart.forEach((product) => {
      message += `${product.title} - R$${product.price}\n`;
    });
    const whatsappNumber = "5521965502519"; 
    const whatsappURL = `https://wa.me/${whatsappNumber}?text=${encodeURIComponent(message)}`;

    window.open(whatsappURL, "_blank");
  };

  const handleContinueShopping = () => {
    // Oculta o carrinho ao clicar em "Continuar comprando"
    setExpandedProduct(null);
    onContinueShopping();
  };

  return (
    <div className={`cart-container ${showCart ? "" : "hidden"}`}>
      <div className="cart-title">Meu Carrinho</div>
      {expandedProduct && ( // Verifica se há um produto expandido
        <div className="expanded-product">
          <div>{expandedProduct.title}</div>
          <div>{expandedProduct.price}</div>
          <button onClick={() => dispatch(removeProduct(expandedProduct))}>
            Remover
          </button>
        </div>
      )}
      {!expandedProduct && (
        <table className="cart-products-table">
          <thead>
            <tr>
              <th></th>
              <th></th>
              <th>Quantidade</th>
              <th>Preço</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {cart.map((product) => (
              <tr key={product.id} onClick={() => handleToggleExpand(product)}>
                <td>
                  <img src={product.image[0]} alt={product.title} />
                </td>
                <td>
                  <strong>{product.title}</strong>
                </td>
                <td className="quantity-container">
                  <RemoveCircleSharpIcon className="quantity-icon" />
                  <span className="quantity">{product.quantity}</span>
                  <AddCircleSharpIcon className="quantity-icon" />
                </td>
                <td>R${product.price}</td>
                <td>
                  <DeleteForeverIcon onClick={() => dispatch(removeProduct(product))} />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <div className="cart-total" data-testid="total">Total: ${total}</div>
      <Link to="/produtos" onClick={handleContinueShopping}>
        Continuar comprando
      </Link>
      <button className="finish-button" onClick={handleSendWhatsAppMessage}>Finalizar Compra</button>
    </div>
  );
};

export default CartComponent;
