import KeyboardArrowLeft from "@mui/icons-material/KeyboardArrowLeft";
import KeyboardArrowRight from "@mui/icons-material/KeyboardArrowRight";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import MobileStepper from "@mui/material/MobileStepper";
import React, { useState } from "react";
import { FiShoppingCart } from "react-icons/fi";
import { useDispatch, useSelector } from "react-redux";
import SwipeableViews from "react-swipeable-views";
import CheckCircleIcon from "@mui/icons-material/CheckCircle";
import { Products } from "../../data/products";
import { addProduct, removeProduct } from "../../redux/Cart/cart-slice";
import { RootReducer } from "../../redux/root-reducer";
import "./ProductCard.css";
import ProductInformation from "../productInformation/productInformation";

interface ProductCardProps {
  product?: Products;
}

const ProductCard: React.FC<ProductCardProps> = ({ product }) => {
  const { cart } = useSelector(
    (rootReducer: RootReducer) => rootReducer.cartReducer
  );
  const dispatch = useDispatch();
  const [activeStep, setActiveStep] = useState(0);
  const [showAddedToCart, setShowAddedToCart] = useState(false);
  const [isFocused, setIsFocused] = useState(false); // Estado local para controlar o foco do carrinho
  const [showCart, setShowCart] = useState(false);

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleFocus = () => {
    setIsFocused(true);
  };

  const toggleCart = () => {
    setShowCart(!showCart);
  };

  // Variável booleana para informar se o produto está no carrinho
  const isProductOnCart =
    product &&
    cart.find((productOnCart) => product.id === productOnCart.id) !== undefined;

  function handleAddProductToCart() {
    dispatch(addProduct(product));
    setShowAddedToCart(true);

    // Esconder a mensagem após alguns segundos (por exemplo, 3 segundos)
    setTimeout(() => {
      setShowAddedToCart(false);
    }, 3000);
  }

  function handleRemoveProductFromCart() {
    dispatch(removeProduct(product));
  }

  if (!product) {
    return <div>Nenhum produto disponível</div>;
  }

  return (
    <div className="card">
      <div
        className={`cart ${showCart && isFocused ? "center" : ""}`}
        onClick={toggleCart}
      >
        {showCart && isFocused && (
          <div className="overlay" onClick={() => setIsFocused(false)}></div>
        )}
        {showCart && (
        <ProductInformation
          showCart={showCart}
          cart={cart}
          onContinueShopping={() => setIsFocused(false)} // Função para remover o foco do carrinho
        />
      )}
        <SwipeableViews
          index={activeStep}
          onChangeIndex={(index) => setActiveStep(index)}
          enableMouseEvents
        >
          {product.image.map((image, index) => (
            <Box
              key={index}
              component="img"
              className="product_image"
              src={image}
              alt={product.title}
              onClick={handleFocus}
            />
          ))}
        </SwipeableViews>
        <div className="container_button">
          <MobileStepper
            steps={product.image.length}
            position="static"
            activeStep={activeStep}
            nextButton={
              <Button
                size="small"
                onClick={handleNext}
                disabled={activeStep === product.image.length - 1}
              >
                <KeyboardArrowRight />
              </Button>
            }
            backButton={
              <Button
                size="small"
                onClick={handleBack}
                disabled={activeStep === 0}
              >
                <KeyboardArrowLeft />
              </Button>
            }
          />
        </div>
      </div>

      <div className="card_title_container">
        <h2 className="product_title">{product.title}</h2>
        <div className="card_container">
          <strong className="card_price">R${product.price}</strong>
        </div>
      </div>
      <div className="card_button_container">
        <Button
          className={
            isProductOnCart
              ? "card_RemoveFromCartButton"
              : "card_AddToCardButton"
          }
          onClick={
            isProductOnCart
              ? handleRemoveProductFromCart
              : handleAddProductToCart
          }
          variant="outlined"
          style={{ background: "#F579B8", color: "black" }}
        >
          {isProductOnCart ? "Remover do carrinho" : "Adicionar ao carrinho"}
          <FiShoppingCart className="card_buttonSVG" />
        </Button>
      </div>
      {/* Renderizar a mensagem temporária */}
      {showAddedToCart && (
        <div className="added-to-cart-message">
          <CheckCircleIcon className="add_img" />
          <p className="add_title">Produto adicionado ao carrinho!</p>
        </div>
      )}
    </div>
  );
};

export default ProductCard;
