import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { Products } from "../../data/products";
import { removeProduct } from "../../redux/Cart/cart-slice";
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import AddCircleSharpIcon from '@mui/icons-material/AddCircleSharp';
import RemoveCircleSharpIcon from '@mui/icons-material/RemoveCircleSharp';
import './productInformation.css';

interface CartProps {
  product: Products | undefined;
  showCart: boolean;
  cart: Products[];
  onContinueShopping: () => void; 
}

const CartComponent: React.FC<CartProps> = ({ product, showCart, cart, onContinueShopping }) => {
  const dispatch = useDispatch();
  const [cartVisible, setCartVisible] = useState(showCart); // Estado para controlar a visibilidade do carrinho

  const total = cart.reduce((totalCart, product) => {
    return totalCart + product.price * (product.quantity ?? 1);
  }, 0);

  const handleContinueShopping = () => {
    setCartVisible(false); // Oculta o carrinho ao clicar em "Continuar comprando"
  };

  return (
    <div className={`cart-container ${cartVisible ? "" : "hidden"}`}>
      <h2 className="product_title">{product.title}</h2>
      <table className="cart-products-table">
        <thead>
          <tr>
            <th></th>
            <th></th>
            <th>Quantidade</th>
            <th>Pre√ßo</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {cart.map((product) => (
            <tr key={product.id}>
              <td>
                <img src={product.image[0]} alt={product.title} />
              </td>
              <td>
                <strong>{product.title}</strong>
              </td>
              <td className="quantity-container">
                <RemoveCircleSharpIcon className="quantity-icon" />
                <span className="quantity">{product.quantity}</span>
                <AddCircleSharpIcon className="quantity-icon" />
              </td>
              <td>R${product.price}</td>
              <td>
                <DeleteForeverIcon onClick={() => dispatch(removeProduct(product))} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="cart-total" data-testid="total">Total: ${total}</div>
      <Link to="/produtos" onClick={() => { handleContinueShopping(); onContinueShopping(); }}>
        Continuar comprando
      </Link>
      <button className="finish-button">Finalizar Compra</button>
    </div>
  );
};

export default CartComponent;
