import React, { useState } from "react";
import Logo from '../../assets/logo.png';
import Cart from '../../assets/carrinho-de-compras.svg'
import HeaderButtonGroup from "../buttonGroup/HeaderButtonGroup";
import CartComponent from "../cart/Cart";
import './Header.css';

function Header() {
  const [showCart, setShowCart] = useState(false); // Estado local para controlar a exibição do carrinho
  const [cart, setCart] = useState([]); // Inicializa a variável cart como uma array vazia


  // Manipulador de eventos para alternar a exibição do carrinho
  const toggleCart = () => {
    setShowCart(!showCart);
  };

  return (
    <div className="header-container">
      <div className="logo">
        <img className="header-logo" src={Logo} alt="Logo" />
      </div>

      <div className="cart" onClick={toggleCart}> 
        <img className="header-cart" src={Cart} alt="Carrinho de Compras"/> 
      </div>
      <HeaderButtonGroup />
      <hr />

      {showCart && <CartComponent showCart={showCart} cart={cart}/>}
    </div>
  ); 
}

export default Header;

