import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { Products } from "../../data/products";
import { removeProduct } from "../../redux/Cart/cart-slice";
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import './Cart.css';
import IconButton from "material-ui/IconButton";


interface CartProps {
  showCart: boolean;
  cart: Products[];
  onContinueShopping: () => void; 
}

const CartComponent: React.FC<CartProps> = ({ showCart, cart, onContinueShopping }) => {
  const dispatch = useDispatch();
  const [cartVisible, setCartVisible] = useState(showCart); // Estado para controlar a visibilidade do carrinho

  const handleSendWhatsAppMessage = () => {
    // L칩gica para criar a mensagem do WhatsApp com os produtos do carrinho e abrir o aplicativo
    let message = `Ol치! Estou interessado nos seguintes produtos:\n`;

    cart.forEach((product) => {
      message += `${product.title} - R$${product.price}\n`;
    });
    const whatsappNumber = "5521965502519"; 
    const whatsappURL = `https://wa.me/${whatsappNumber}?text=${encodeURIComponent(message)}`;

    window.open(whatsappURL, "_blank");
  };


  const total = cart.reduce((totalCart, product) => {
    return totalCart + product.price * (product.quantity ?? 1);
  }, 0);

  const handleContinueShopping = () => {
    setCartVisible(false); // Oculta o carrinho ao clicar em "Continuar comprando"
  };


  return (
    <div className={`cart-container ${cartVisible ? "" : "hidden"}`}>
      <div className="cart-title">Meu Carrinho</div>
      <div className="cart-products-list">
        {cart.map((product) => (
          <div className="cart-products-item" key={product.id}>
            <div className="product-image">
              <img src={product.image[0]} alt={product.title} />
            </div>
            <div className="product-details">
              <strong>{product.title}</strong>
              <span className="product-price">R${product.price.toFixed(2)}</span>
              <div className="quantity-selector">
                <QuantitySelector
                  quantity={product.quantity}
                  onDecrease={handleDecreaseQuantity(product)}
                  onIncrease={handleIncreaseQuantity(product)}
                />
              </div>
              <div className="remove-button">
                <IconButton onClick={() => dispatch(removeProduct(product))} aria-label="Remover Produto">
                  <DeleteForeverIcon />
                </IconButton>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className="cart-total" data-testid="total">Total: ${total.toFixed(2)}</div>

      {/* Adicione um link para continuar comprando */}
      <Link to="/produtos" onClick={() => { handleContinueShopping(); onContinueShopping(); }}>
        Continuar comprando
      </Link>   
      {/* Bot칚o para enviar mensagem pelo WhatsApp */}
      <button onClick={handleSendWhatsAppMessage}>Enviar por WhatsApp</button> 
    </div>
  );
};

export default CartComponent;
