import DeleteForeverIcon from "@mui/icons-material/DeleteForever";
import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { Products } from "../../data/products";
import { removeProduct, addProduct } from "../../redux/Cart/cart-slice"; // Importando addProduct
import { useSelector } from "react-redux";
import { RootReducer } from "../../redux/root-reducer";
import { ButtonGroup, Button, TextField } from "@mui/material";
import Badge from "@mui/material/Badge";
import { Link } from "react-router-dom";

import "./ClientCheckout.css";

interface CartProps {
  cart?: Products[];
  onContinueShopping: () => void;
}

const ClientCheckout: React.FC<CartProps> = ({ onContinueShopping }) => {
  const dispatch = useDispatch();
  const cart: Products[] = useSelector((state: RootReducer) => state.cart.cart);

  const handleSendWhatsAppMessage = () => {
    // L√≥gica para criar a mensagem do WhatsApp com os produtos do carrinho e abrir o aplicativo
    let message = `Ol√°! üòä Estou super empolgado para fazer algumas comprinhas! Aqui est√£o os produtos que desejo:\n\n`;

    cart.forEach((product, index) => {
      message += `${index + 1}. *${product.title}* - R$${product.price.toFixed(2)}\n`; // Usando n√∫meros para enumerar os produtos e pre√ßo em negrito
    });

    const whatsappNumber = "5521965502519";
    const whatsappURL = `https://wa.me/${whatsappNumber}?text=${encodeURIComponent(
      message
    )}`;

    window.open(whatsappURL, "_blank");
};

  const handleQuantityChange = (productId: number, newQuantity: number) => {
    // Atualiza a quantidade do produto no carrinho
    const updatedCart = cart.map((product) =>
      product.id === productId ? { ...product, quantity: newQuantity } : product
    );

    // Dispara a a√ß√£o para atualizar o carrinho no Redux
    dispatch(addProduct(updatedCart));
  };

  const total = cart
    .reduce((totalCart, product) => {
      console.log("Product:", product);
      const productPrice = parseFloat(String(product.price ?? 0)); // Convertendo para n√∫mero e definindo um valor padr√£o se price for undefined
      const productQuantity = parseInt(String(product.quantity ?? 1)); // Convertendo para n√∫mero e definindo um valor padr√£o se quantity for undefined
      console.log("Price:", productPrice);
      console.log("Quantity:", productQuantity);
      return totalCart + productPrice * productQuantity;
    }, 0)
    .toFixed(2);

  return (
    <div className="checkout-container">
      <div className="cart-title-checkout">Meu Carrinho</div>
      <hr />
      <table className="cart-products-table">
        <tbody>
          {cart &&
            cart.map((product) => (
              <tr key={product.id}>
                <td>
                  <div
                    style={{ position: "relative", display: "inline-block" }}
                  >
                    <Badge badgeContent={product.quantity} color="primary">
                      {/* Imagem do produto */}
                      {product.image &&
                        product.image.length > 0 &&
                        product.image[0] && (
                          <img src={product.image[0]} alt={product.title} />
                        )}
                    </Badge>
                  </div>
                </td>
                <td>
                  <strong className="strong_title">{product.title}</strong>
                </td>

                <td>
                  <ButtonGroup>
                    <Button
                      onClick={() =>
                        handleQuantityChange(
                          product.id,
                          (product.quantity ?? 1) - 1 // Defina um valor padr√£o para a quantidade
                        )
                      }
                      disabled={(product.quantity ?? 1) <= 1} // Defina um valor padr√£o para a quantidade
                    >
                      -
                    </Button>

                    <Button
                      onClick={() =>
                        handleQuantityChange(
                          product.id,
                          (product.quantity ?? 1) + 1 // Defina um valor padr√£o para a quantidade
                        )
                      }
                    >
                      +
                    </Button>
                  </ButtonGroup>
                </td>
                <td className="td_price">
                  R${(product.price ?? 0).toFixed(2)}
                </td>
                <td>
                  <DeleteForeverIcon
                    className="delete_icon"
                    onClick={() => dispatch(removeProduct(product))}
                  />
                </td>
              </tr>
            ))}
        </tbody>
      </table>
      <hr />
      <div className="cart-total-container">
        <div className="cart-total" data-testid="total">
          Total: R${parseFloat(total).toFixed(2)}
        </div>
      </div>
      <div className="buttons_finished">
      <button className="finish-button">
          <Link
            className="link-button"
            to="/produtos"
            onClick={() => {
/*               handleContinueShopping();
 */              onContinueShopping();
            }}
          >
            Continuar comprando
          </Link>
        </button>
        <button className="finish-button" onClick={handleSendWhatsAppMessage}>
          Finalizar Compra
        </button>
      </div>
    </div>
  );
};

export default ClientCheckout;
