import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Products } from "../../data/products";
import { removeProduct } from "../../redux/Cart/cart-slice";
import './Cart.css';

interface CartProps {
  showCart: boolean;
  cart: Products[];
}

const CartComponent: React.FC<CartProps> = ({ showCart, cart }) => {
  const [isFocused, setIsFocused] = useState(false); // Estado local para controlar o foco do carrinho
  const dispatch = useDispatch();

  const total = cart.reduce((totalCart, product) => {
    return totalCart + product.price;
  }, 0);

  const handleFocus = () => {
    setIsFocused(true);
  };

  return (
    <div className={`cart-container ${showCart && isFocused ? "center" : ""}`}>
      {/* Adiciona a camada de fundo semi-transparente quando o carrinho est√° em foco */}
      {showCart && isFocused && <div className="overlay" onClick={() => setIsFocused(false)}></div>}

      <div className="cart-title" onClick={handleFocus}>Carrinho</div>

      <div className="cart-products-list">
        {cart.map((product) => (
          <div className="cart-products-item" key={product.id}>
            <strong>{product.title}</strong> - {product.price}
            <button onClick={() => dispatch(removeProduct(product))}>
              Remover
            </button>
          </div>
        ))}
      </div>

      <div className="cart-total" data-testid="total">Total: ${total}</div>
    </div>
  );
};

export default CartComponent;
