import DeleteForeverIcon from "@mui/icons-material/DeleteForever";
import React from 'react';
import { useDispatch } from 'react-redux';
import { Products } from "../../data/products";
import { removeProduct } from "../../redux/Cart/cart-slice";
import { useSelector } from "react-redux";
import { RootReducer } from "../../redux/root-reducer";
import './ClientChe'

interface CartProps {
  cart?: Products[];
  onContinueShopping: () => void;
}

const ClientCheckout: React.FC<CartProps> = ({ onContinueShopping }) => {
  const dispatch = useDispatch();
  const cart: Products[] = useSelector((state: RootReducer) => state.cart.cart);

  const handleSendWhatsAppMessage = () => {
    // L칩gica para criar a mensagem do WhatsApp com os produtos do carrinho e abrir o aplicativo
    let message = `Ol치! Estou interessado nos seguintes produtos:\n`;

    cart.forEach((product) => {
      message += `${product.title} - R$${product.price.toFixed(2)}\n`;
    });
    const whatsappNumber = "5521965502519";
    const whatsappURL = `https://wa.me/${whatsappNumber}?text=${encodeURIComponent(
      message
    )}`;

    window.open(whatsappURL, "_blank");
  };

  const total = cart.reduce((totalCart, product) => {
    return totalCart + product.price * (product.quantity ?? 1);
  }, 0);

  return (
    <div className="checkout-container">
      <div className="checkout-title">Meu Carrinho</div>
      <hr />
      <table className="checkout-products-table">
        <tbody>
          {cart.map((product) => (
            <tr key={product.id}>
              <td>
                <img src={product.image[0]} alt={product.title} />
              </td>
              <td>
                <strong className="checkout-strong-title">{product.title}</strong>
              </td>
              <td className="checkout-td-price">R${product.price.toFixed(2)}</td>
              <td>
                <DeleteForeverIcon
                  className="checkout-delete-icon"
                  onClick={() => dispatch(removeProduct(product))}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <hr />
      <div className="checkout-total-container">
        <div className="checkout-total" data-testid="total">
          Total: ${total.toFixed(2)}
        </div>
      </div>
      <div className="checkout-buttons-container">
        <button className="checkout-finish-button" onClick={onContinueShopping}>
          Continuar comprando
        </button>
        <button className="checkout-finish-button" onClick={handleSendWhatsAppMessage}>
          Finalizar Compra
        </button>
      </div>
    </div>
  );
};

export default ClientCheckout;
