import React, { useState } from "react";
import Logo from "../../assets/logo.png";
import Cart from "../../assets/carrinho-de-compras.svg";
import HeaderButtonGroup from "../buttonGroup/HeaderButtonGroup";
import CartComponent from "../cart/Cart";
import "./Header.css";
import { RootReducer } from "../../redux/root-reducer";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";

function Header() {
  const { cart } = useSelector(
    (rootReducer: RootReducer) => rootReducer.cartReducer
  );
  const dispatch = useDispatch();

  const [showCart, setShowCart] = useState(false);
  const [isFocused, setIsFocused] = useState(false); // Estado local para controlar o foco do carrinho

  const toggleCart = () => {
    setShowCart(!showCart);
  };

  const handleFocus = () => {
    setIsFocused(true);
  };
  const itemCount = cart.reduce((total, product) => total + product.quantity, 0);

  return (
    <div className="header-container">
      <div className="logo">
        <img className="header-logo" src={Logo} alt="Logo" />
      </div>

      <div
        className={`cart ${showCart && isFocused ? "center" : ""}`}
        onClick={toggleCart}
      >
        {showCart && isFocused && (
          <div className="overlay" onClick={() => setIsFocused(false)}></div>
        )}

        <img
          className="header-cart"
          src={Cart}
          alt="Carrinho de Compras"
          onClick={handleFocus}
        />
      </div>
      <HeaderButtonGroup />
      <hr />

      {showCart && <CartComponent showCart={showCart} cart={cart} />}
    </div>
  );
}

export default Header;

