import * as React from 'react';
import { useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import SwipeableViews from 'react-swipeable-views';
import { autoPlay } from 'react-swipeable-views-utils';
import { Typography } from '@mui/material';


import './CarouselDepoiments.css';

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

const testimonials = [
  {
    name: 'Rogério',
    pet: 'Thor (Cachorro)',
    testimony: 'A melhor parte de poder contar com um veterinário à domicílio é que nosso pet fica à vontade. Dessa forma, não causa nenhum desconforto ou insegurança, e nosso bichinho fica sempre bem.',
  },
  {
    name: 'Ana Paula',
    pet: 'Mel (Gata)',
    testimony: 'Ter um atendimento especializado em casa fez toda a diferença. A Mel se estressa facilmente, então evitar o transporte e a clínica deixou ela muito mais tranquila.',
  },
  {
    name: 'Lucas',
    pet: 'Bob (Papagaio)',
    testimony: 'O cuidado com aves é muito específico, e o atendimento foi excelente. Saber que o veterinário entende as necessidades do Bob me deixou muito mais seguro.',
  },
  {
    name: 'Fernanda',
    pet: 'Simba (Cachorro)',
    testimony: 'Simba sempre teve medo de veterinário, mas o atendimento em casa mudou isso. O carinho e paciência da equipe fizeram toda a diferença.',
  },
  {
    name: 'Marcos',
    pet: 'Nino (Hamster)',
    testimony: 'Nunca pensei que um veterinário viria até nossa casa para cuidar de um hamster. O atendimento foi super profissional e cuidadoso, recomendo muito!',
  },
];

export default function CarouselDepoiments() {
  const theme = useTheme();
  const [activeStep, setActiveStep] = React.useState(0);

  const handleStepChange = (step) => {
    setActiveStep(step);
  };

  return (
    <Box 
      sx={{
        position: 'relative',
        overflow: 'hidden', 
        display: 'flex', // Ajuste para centralizar o carousel
        justifyContent: 'center',
        alignItems: 'center',
        height: '50rem', // Altura total ajustada do carousel
      }}
    >
      {/* Div com a imagem de fundo */}
      <Box
        sx={{
          backgroundPosition: 'center', 
          height: '100%', // A altura do contêiner agora ocupa 100% da altura definida acima
          display: 'flex',
          alignItems: 'center', // Centraliza verticalmente o conteúdo
          justifyContent: 'center', // Centraliza horizontalmente o conteúdo
          width: '100%', // Garante que o fundo cubra todo o espaço horizontal
        }}
      >
        <AutoPlaySwipeableViews
          axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
          index={activeStep}
          onChangeIndex={handleStepChange}
          enableMouseEvents
        >
          {testimonials.map((testimonial, index) => (
            <Box
              key={index}
              sx={{
                display: 'flex', // Usar flex para garantir que o conteúdo também fique centralizado dentro do box
                flexDirection: 'column',
                alignItems: 'center', // Centraliza horizontalmente o conteúdo do depoimento
                justifyContent: 'center', // Centraliza verticalmente o conteúdo do depoimento
                padding: '10px', // Padding reduzido
                borderRadius: '8px',
                boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.25)',
                color: '#f1f1f1',
                textAlign: 'center',
                maxWidth: '30rem', // Largura máxima ajustada para centralizar melhor o conteúdo
              }}
            >
              <Typography variant="h1" sx={{ fontStyle: 'normal', fontWeight: 'bold', color: '#FF6F61', fontSize: '3rem' }}>
                &rdquo;
              </Typography>
              <Typography variant="h2" sx={{ fontSize: '2rem', fontFamily: 'Barlow Semi Condensed', fontWeight: 'bold', color: '#B2F9B2', marginBottom: '0.5rem' }}>
                {testimonial.name}
              </Typography>
              <Typography variant="h3" sx={{ fontSize: '1.5rem', fontFamily: 'Barlow Semi Condensed', color: '#B2F9B2', marginBottom: '1.5rem' }}>
                {testimonial.pet}
              </Typography>
              <Typography variant="body1" sx={{ fontSize: '1.2rem', fontFamily: 'Barlow Semi Condensed', fontStyle: 'italic', color: '#D3D3D3',  lineHeight: 1.4 }}>
                {testimonial.testimony}
              </Typography>              
            </Box>
          ))}
        </AutoPlaySwipeableViews>
      </Box>
    </Box>
  );
}
