import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { Products } from "../../data/products";
import { removeProduct } from "../../redux/Cart/cart-slice";
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import AddCircleSharpIcon from '@mui/icons-material/AddCircleSharp';
import RemoveCircleSharpIcon from '@mui/icons-material/RemoveCircleSharp';
import './productInformation.css';

interface ProductInformationProps {
  product?: Products;
  showCart: boolean;
  cart: Products[];
  onContinueShopping: () => void; 
}

const ProductInformation: React.FC<ProductInformationProps> = ({ product, showCart, cart, onContinueShopping }) => {
  const dispatch = useDispatch();
  const [cartVisible, setCartVisible] = useState(showCart); // Estado para controlar a visibilidade do carrinho

  const total = cart.reduce((totalCart, product) => {
    return totalCart + product.price * (product.quantity ?? 1);
  }, 0);

  const handleContinueShopping = () => {
    setCartVisible(false); // Oculta o carrinho ao clicar em "Continuar comprando"
  };

  return (
    <div className={`cart-container ${cartVisible ? "" : "hidden"}`}>
      {product && ( // Verificação de nulidade para 'product'
        <h2 className="product_title">{product.title}</h2>
      )}
    <div>
      <div></div>
      <div></div>
      <div></div>
      <div></div>
      <div></div>
      <div></div>
    </div>
    <div><img src={product?.image[0]} alt={product?.title} /></div>
    <div>
      <p>{product?.description}</p>
      <strong>{product?.price}</strong>
      <button>Adicionar ao Carrinho</button>
      <button>Ver Carrinho</button>
      <button>Continuar comprando</button>
    </div>

    <Link to="/produtos" onClick={() => { handleContinueShopping(); onContinueShopping(); }}>
  <button className="finish-button">Continuar Comprando</button>
</Link>
      <button className="finish-button">Adicionar ao Carr</button>
      <button className="finish-button">Finalizar Compra</button>
    </div>
  );
};

export default ProductInformation;
