import React, { useRef } from "react";
import emailjs from "emailjs-com"; // Adicionando o EmailJS
import TextField from "@material-ui/core/TextField";
import { Box, Button } from "@mui/material";
import Gmail from "../../assets/";
import "./Contato.css";

export default function Contato() {
  const formRef = useRef<HTMLFormElement>(null); // Adiciona a referência ao formulário

  const sendEmail = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    emailjs
      .sendForm(
        'service_px5yag2', // substitua pelo ID do serviço de email
        'template_uuquj9e', // substitua pelo ID do template
        e.target as HTMLFormElement, // Define o target como HTMLFormElement
        'eEU9e5NvGB1WAKPva' // substitua pelo seu User ID
      )
      .then(
        (result) => {
          console.log(result.text);
          alert('Mensagem enviada com sucesso!');
          formRef.current?.reset(); // Limpa o formulário após o envio
        },
        (error) => {
          console.log(error.text);
          alert('Ocorreu um erro ao enviar a mensagem.');
        }
      );
  };

  return (
    <div className="container-form">
      <h1>Contato</h1>
      <div className="container-main-form">
        <div className="title-subtitle">
          <h1>Envie-nos uma mensagem</h1>
          <p className="subtitle">Se precisar de alguma ajuda, estamos à sua disposição.</p>

          <form ref={formRef} onSubmit={sendEmail}> {/* Adiciona a referência ao formulário */}
            <Box className="container-box-primary">
              <Box p={3} className="container-dados">
                <div className="text">
                  <TextField fullWidth label="Nome" name="name" required />
                  <TextField fullWidth label="Email" name="email" required />
                  <TextField
                    fullWidth
                    label="DDD + Telefone ou Celular"
                    name="phone"
                    required
                  />
                  <TextField
                    fullWidth
                    label="Mensagem"
                    name="message"
                    multiline
                    rows={4}
                    required
                  />
                </div>
              </Box>
            </Box>

            <div className="button">
              <button className="button-form-dados" type="submit">
                <img
                  src={Gmail}
                  alt="Enviar mensagem"
                  style={{ marginRight: "8px", width: "20px", height: "20px" }}
                />
                Enviar Mensagem
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
