import React from "react";
import { useParams } from "react-router-dom";
import articlesData from "../../data/articlesData";

const ArticleDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>(); // Pegue o ID da URL
  if (!id) {
    return <div>Artigo não encontrado.</div>; // Mensagem de erro se o ID não for encontrado
  }

  const article = articlesData.find(
    (article: { id: number }) => article.id === parseInt(id)
  );

  if (!article) {
    return <div>Artigo não encontrado.</div>; // Mensagem de erro se o artigo não for encontrado
  }

  return (
    <div className="article-detail-container">
      <h1>{article.title}</h1>
      <h2>{article.subtitle01}</h2>
      <p>{article.content01}</p>
      <hr />
      {/* Bloco de texto e imagem */}
      <div className="image-text-block">
        <div className="text-side">
          <h2>{article.subtitle02}</h2>
          <div className="image-text-block">
            {" "}
            <div className="text-side">
              {" "}
              <h3>{article.subtitle03}</h3>
              <p>{article.content02}</p>
              <p>{article.content03}</p>{" "}
            </div>{" "}
            <div className="image-side">
              {" "}
              <img src={article.images[0]} alt={article.title} />{" "}
            </div>{" "}
          </div>{" "}
          <div className="image-text-block reverse">
            {" "}
            <div className="image-side">
              {" "}
              <img src={article.images[1]} alt={article.title} />{" "}
            </div>{" "}
            <div className="text-side">
              {" "}
              <h3>{article.subtitle04}</h3> <p>{article.content04}</p>{" "}
            </div>{" "}
            <hr />
          </div>
        </div>
        <div className="topic-02">
          <h3>{article.subtitle05}</h3>
          <p>{article.content05}</p>
          <p>{article.content06}</p>
        </div>
        <hr />
      </div>

      {/* Exemplos de casos */}
      <h3>Exemplos de Casos</h3>
      <ol>
        {article.examples.map((example: string, index: number) => (
          <li key={index}>
            <h4>{article.examplesTitle[index]} </h4> {example}
          </li>
        ))}
      </ol>
      <hr />


      {/* Vídeo incorporado */}
      <div className="video-container">
        <h3>Assista ao vídeo:</h3>
        <iframe
          width="560"
          height="315"
          src={article.videoUrl}
          title="YouTube video player"
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></iframe>
      </div>

      {/* Artigos relacionados */}
      {article.relatedArticles && article.relatedArticles.length > 0 && (
        <>
          <h3>Artigos Relacionados</h3>
          <ul>
            {article.relatedArticles.map(
              (relatedArticle: string, index: number) => (
                <li key={index}>{relatedArticle}</li>
              )
            )}
          </ul>
        </>
      )}
    </div>
  );
};

export default ArticleDetail;
