import React from 'react';
import { useParams } from 'react-router-dom';
import articlesData from '../data/articlesData'; // Ajuste o caminho conforme necessário

const ArticleDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>(); // Pegue o ID da URL
  const article = articlesData.find(article => article.id === parseInt(id));

  if (!article) {
    return <div>Artigo não encontrado.</div>; // Mensagem de erro se o artigo não for encontrado
  }

  return (
    <div>
      <h1>{article.title}</h1>
      <p>{article.content}</p>
      {/* Adicione mais detalhes conforme necessário */}
    </div>
  );
};

export default ArticleDetails;
