import DeleteForeverIcon from "@mui/icons-material/DeleteForever";
import React from "react";
import { useDispatch } from "react-redux";
import { Products } from "../../data/products";
import { removeProduct } from "../../redux/Cart/cart-slice";
import { useSelector } from "react-redux";
import { RootReducer } from "../../redux/root-reducer";
import { ButtonGroup, Button, TextField } from "@mui/material";
import "./ClientCheckout.css";

interface CartProps {
  cart?: Products[];
  onContinueShopping: () => void;
}

const ClientCheckout: React.FC<CartProps> = ({ onContinueShopping }) => {
  const dispatch = useDispatch();
  const cart: Products[] = useSelector((state: RootReducer) => state.cart.cart);

  const handleSendWhatsAppMessage = () => {
    // Lógica para criar a mensagem do WhatsApp com os produtos do carrinho e abrir o aplicativo
    let message = `Olá! Estou interessado nos seguintes produtos:\n`;

    cart.forEach((product) => {
      message += `${product.title} - R$${product.price.toFixed(2)}\n`;
    });
    const whatsappNumber = "5521965502519";
    const whatsappURL = `https://wa.me/${whatsappNumber}?text=${encodeURIComponent(
      message
    )}`;

    window.open(whatsappURL, "_blank");
  };

  const handleQuantityChange = (productId: number, newQuantity: number) => {
    // Atualiza a quantidade do produto no carrinho
    const updatedCart = cart.map((product) =>
      product.id === productId ? { ...product, quantity: newQuantity } : product
    );
    // Dispara a ação para atualizar o carrinho no Redux
    // Esta ação deve ser implementada no seu arquivo cart-slice.ts
    // dispatch(updateCart(updatedCart));
  };

  const total = cart.reduce((totalCart, product) => {
    return totalCart + product.price * (product.quantity ?? 1);
  }, 0);

  return (
    <div className="checkout-container">
      <div className="cart-title-checkout">Meu Carrinho</div>
      <hr />
      <table className="cart-products-table">
        <tbody>
          {cart.map((product) => (
            <tr key={product.id}>
              <td>
                <img src={product.image[0]} alt={product.title} />
              </td>
              <td>
                <strong className="strong_title">{product.title}</strong>
              </td>
              <td className="td_price">R${product.price.toFixed(2)}</td>
              <td>
                <input
                  type="number"
                  min="1"
                  value={product.quantity}
                  onChange={(e) =>
                    handleQuantityChange(product.id, parseInt(e.target.value))
                  }
                />
              </td>
              <td>
                <DeleteForeverIcon
                  className="delete_icon"
                  onClick={() => dispatch(removeProduct(product))}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <hr />
      <div className="cart-total-container">
        <div className="cart-total" data-testid="total">
          Total: ${total.toFixed(2)}
        </div>
      </div>
      <div className="buttons_finished">
        <button className="finish-button" onClick={onContinueShopping}>
          Continuar comprando
        </button>
        <button className="finish-button" onClick={handleSendWhatsAppMessage}>
          Finalizar Compra
        </button>
      </div>
    </div>
  );
};

export default ClientCheckout;
