import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { Products } from "../../data/products";
import { removeProduct } from "../../redux/Cart/cart-slice";
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import './Cart.css';

interface CartProps {
  showCart: boolean;
  cart: Products[];
  onContinueShopping: () => void; 
}

const CartComponent: React.FC<CartProps> = ({ showCart, cart, onContinueShopping }) => {
  const dispatch = useDispatch();
  const [cartVisible, setCartVisible] = useState(showCart); // Estado para controlar a visibilidade do carrinho

  const handleSendWhatsAppMessage = () => {
    // Lógica para criar a mensagem do WhatsApp com os produtos do carrinho e abrir o aplicativo
    let message = `Olá! Estou interessado nos seguintes produtos:\n`;

    cart.forEach((product) => {
      message += `${product.title} - R$${product.price}\n`;
    });
    const whatsappNumber = "5521965502519"; 
    const whatsappURL = `https://wa.me/${whatsappNumber}?text=${encodeURIComponent(message)}`;

    window.open(whatsappURL, "_blank");
  };


  const total = cart.reduce((totalCart, product) => {
    return totalCart + product.price * (product.quantity ?? 1);
  }, 0);

  const handleContinueShopping = () => {
    setCartVisible(false); // Oculta o carrinho ao clicar em "Continuar comprando"
  };


  return (
    <div className={`cart-container ${cartVisible ? "" : "hidden"}`}>
      <div className="cart-title">Meu Carrinho</div>
      <table className="cart-products-table">
        <thead>
          <tr>
            <th></th>
            <th></th>
            <th>Quantidade</th>
            <th>Preço</th>
            <th>Ação</th>
          </tr>
        </thead>
        <tbody>
          {cart.map((product) => (
            <tr key={product.id}>

              <td>im{product.image[0]}</td>
              <td><strong>{product.title}</strong></td>
              <td>{product.quantity}</td>
              <td>R${product.price}</td>
              <td>
                <DeleteForeverIcon onClick={() => dispatch(removeProduct(product))} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="cart-total" data-testid="total">Total: ${total}</div>
      <Link to="/produtos" onClick={() => { handleContinueShopping(); onContinueShopping(); }}>
        Continuar comprando
      </Link>
      <button className="finish-button" onClick={handleSendWhatsAppMessage}>Finalizar Compra</button>
    </div>
  );
};

export default CartComponent;
