import DeleteForeverIcon from "@mui/icons-material/DeleteForever";
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { Products } from "../../data/products";
import { removeProduct } from "../../redux/Cart/cart-slice";
import "./Cart.css";

interface CartProps {
  showCart?: boolean;
  cart: Products[];
  onContinueShopping: () => void;
}

const CartComponent: React.FC<CartProps> = ({
  showCart,
  cart,
  onContinueShopping,
}) => {
  const dispatch = useDispatch();
  const [cartVisible, setCartVisible] = useState(showCart); // Estado para controlar a visibilidade do carrinho

  const total = cart.reduce((totalCart, product) => {
    const productPrice = product.price ?? 0; // Defina um valor padrão se price for undefined
    const productQuantity = product.quantity ?? 1; // Defina um valor padrão se quantity for undefined
    return totalCart + (productPrice * productQuantity);
  }, 0).toFixed(2);

  const handleContinueShopping = () => {
    setCartVisible(false); // Oculta o carrinho ao clicar em "Continuar comprando"
  };

  return (
    <div className={`cart-container ${cartVisible ? "" : "hidden"}`}>
      <div className="cart-title">Meu Carrinho</div>
      <hr />
      <table className="cart-products-table">
        <tbody>
          {cart &&
            cart.map((product) => (
              <tr key={product.id}>
                <td>
                  {product.image &&
                    product.image.length > 0 &&
                    product.image[0] && (
                      <img src={product.image[0]} alt={product.title} />
                    )}
                </td>
                <td>
                  <strong className="strong_title">{product.title}</strong>
                </td>
                <td className="td_price">
                  {product.price
                    ? `R$${product.price.toFixed(2)}`
                    : "Preço Indisponível"}
                </td>
                <td>
                  <DeleteForeverIcon
                    className="delete_icon"
                    onClick={() => dispatch(removeProduct(product))}
                  />
                </td>
              </tr>
            ))}
        </tbody>
      </table>
      <hr />
      <div className="cart-total-container">
        <div className="cart-total" data-testid="total">
          Total: R${parseFloat(total).toFixed(2)}
        </div>
      </div>
      <div className="buttons_finished">
        <button className="finish-button">
          <Link
            className="link-button"
            to="/produtos"
            onClick={() => {
              handleContinueShopping();
              onContinueShopping();
            }}
          >
            Continuar comprando
          </Link>
        </button>
        <button className="finish-button">
          <Link
            className="link-button"
            to="/checkout"
            onClick={() => {
              handleContinueShopping();
              onContinueShopping();
            }}
          >
            Avançar
          </Link>
        </button>
      </div>
    </div>
  );
};

export default CartComponent;
