import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { Products } from "../../data/products";
import { removeProduct, updateQuantity } from "../../redux/Cart/cart-slice";
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import QuantitySelector from "../";
import './Cart.css';

interface CartProps {
  showCart: boolean;
  cart: Products[];
  onContinueShopping: () => void; 
}

const CartComponent: React.FC<CartProps> = ({ showCart, cart, onContinueShopping }) => {
  const dispatch = useDispatch();
  const [cartVisible, setCartVisible] = useState(showCart); 

  const handleSendWhatsAppMessage = () => {
    let message = `Olá! Estou interessado nos seguintes produtos:\n`;

    cart.forEach((product) => {
      message += `${product.title} - R$${product.price}\n`;
    });
    const whatsappNumber = "5521965502519"; 
    const whatsappURL = `https://wa.me/${whatsappNumber}?text=${encodeURIComponent(message)}`;

    window.open(whatsappURL, "_blank");
  };

  const total = cart.reduce((totalCart, product) => {
    return totalCart + product.price * (product.quantity ?? 1);
  }, 0);

  const handleContinueShopping = () => {
    setCartVisible(false);
    onContinueShopping();
  };

  const handleRemoveProduct = (product: Products) => {
    dispatch(removeProduct(product));
  };

  const handleDecreaseQuantity = (product: Products) => {
    const newQuantity = product.quantity - 1;
    if (newQuantity > 0) {
      dispatch(updateQuantity({ id: product.id, quantity: newQuantity }));
    }
  };

  const handleIncreaseQuantity = (product: Products) => {
    const newQuantity = product.quantity + 1;
    dispatch(updateQuantity({ id: product.id, quantity: newQuantity }));
  };

  return (
    <div className={`cart-container ${cartVisible ? "" : "hidden"}`}>
      <div className="cart-title">Meu Carrinho</div>
      <div className="cart-products-list">
        {cart.map((product) => (
          <CartItem
            key={product.id}
            product={product}
            onRemove={handleRemoveProduct}
            onDecreaseQuantity={() => handleDecreaseQuantity(product)}
            onIncreaseQuantity={() => handleIncreaseQuantity(product)}
          />
        ))}
      </div>
      <div className="cart-total" data-testid="total">Total: ${total}</div>
      <Link to="/produtos" onClick={handleContinueShopping}>Continuar comprando</Link>
      <button onClick={handleSendWhatsAppMessage}>Enviar por WhatsApp</button>
    </div>
  );
};

export default CartComponent;
