import { createSlice } from "@reduxjs/toolkit";
import { Products } from "../../data/products";

interface CartState {
  cart: Products[];
}

const initialState: CartState = {
  cart: [],
};

export const cartSlice = createSlice({
  initialState,
  name: "cart",
  reducers: {
    addProduct: (state, action) => {
      const productToAdd = action.payload;
      const existingProductIndex = state.cart.findIndex(
        (product) => product.id === productToAdd.id
      );

      if (existingProductIndex !== -1) {
        // Se o produto já estiver no carrinho, incremente a quantidade
        state.cart[existingProductIndex].quantity += 1;
      } else {
        // Se o produto não estiver no carrinho, adicione-o com quantidade 1
        state.cart.push({ ...productToAdd, quantity: 1 });
      }
    },
    removeProduct: (state, action) => {
      const productToRemove = action.payload;
      const cartFiltered = state.cart.filter(
        (product) => product.id !== productToRemove.id
      );
      state.cart = cartFiltered;
    },
  },
});

export const { addProduct, removeProduct } = cartSlice.actions;
