import React from "react";
import { Link } from "react-router-dom";
import { Products } from "../../data/products";
import "./productInformation.css";

interface ProductInformationProps {
  product?: Products;
  showCart: boolean; // Adicione a propriedade showCart aqui
  cart: Products[];
  onContinueShopping: () => void;
/*   onAddToCart: () => void;
 */}

const ProductInformation: React.FC<ProductInformationProps> = ({
  product,
  onContinueShopping,
/*   onAddToCart,
 */}) => {
  return (
    <div className="product-information">
      {product && (
        <>
          <h2 className="product-title">{product.title}</h2>
          <div className="image-container">
            <img src={product.image[0]} alt={product.title} className="main-image" />
            <div className="thumbnail-container">
              {product.image.map((image, index) => (
                <img key={index} src={image} alt={product.title} className="thumbnail-image" />
              ))}
            </div>
          </div>
          <div className="product-details">
            <p className="description">{product.description}</p>
            <strong className="price">R$ {product.price.toFixed(2)}</strong>
            <button className="action-button">
              Adicionar ao Carrinho
            </button>
            <Link to="/cart" onClick={onContinueShopping}>
              <button className="action-button">Ver Carrinho</button>
            </Link>
          </div>
        </>
      )}
    </div>
  );
};

export default ProductInformation;
