import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Products } from "../../data/products";
import { removeProduct } from "../../redux/Cart/cart-slice";
import './Cart.css';

interface CartProps {
  cart: Products[];
  onContinueShopping: () => void; 
}

const CartComponent: React.FC<CartProps> = ({ cart, onContinueShopping }) => {
  const dispatch = useDispatch();
  const [cartVisible, setCartVisible] = useState(true); // Inicializa como true
  const [isFocused, setIsFocused] = useState(true); 

  const total = cart.reduce((totalCart, product) => {
    return totalCart + product.price * (product.quantity ?? 1);
  }, 0);

  const handleContinueShopping = () => {
    setCartVisible(false); // Oculta o carrinho ao clicar em "Continuar comprando"
    onContinueShopping(); // Chama a função passada por props para continuar comprando
  };

  const handleFocusRemove = () => {
    setIsFocused(false);
  };

  const handleSendWhatsAppMessage = () => {
    const message = `Olá! Estou interessado nos seguintes produtos:\n`;

    cart.forEach((product) => {
      message += `${product.title} - R$${product.price}\n`;
    });

    const whatsappURL = `https://wa.me/?text=${encodeURIComponent(message)}`;

    window.open(whatsappURL, "_blank");
  };

  return (
    <div className={`cart-container ${cartVisible ? "" : "hidden"}`}> {/* Aplica a classe "hidden" se cartVisible for falso */}
      <div className="cart-title">Carrinho</div>
      <div className="cart-products-list">
        {cart.map((product) => (
          <div className="cart-products-item" key={product.id}>
            <strong>{product.title}</strong> - {product.price}
            <button onClick={() => dispatch(removeProduct(product))}>
              Remover
            </button>
          </div>
        ))}
      </div>
      <div className="cart-total" data-testid="total">Total: ${total}</div>

      {/* Adiciona um botão para enviar por WhatsApp */}
      <button onClick={handleSendWhatsAppMessage}>Enviar por WhatsApp</button>

      {/* Adicione um link para continuar comprando */}
      <button onClick={handleContinueShopping}>Continuar comprando</button>
    </div>
  );
};

export default CartComponent;
