import React, { useEffect, useState } from "react";
import Logo from "../../assets/logo.png";
import HeaderButtonGroup from "../buttonGroup/HeaderButtonGroup";
import CartComponent from "../cart/Cart";
import "./Header.css";
import { RootReducer } from "../../redux/root-reducer";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import Badge from "@mui/material/Badge";

function Header() {
  const { cart } = useSelector(
    (rootReducer: RootReducer) => rootReducer.cartReducer
  );
  const dispatch = useDispatch();

  const [showCart, setShowCart] = useState(false);
  const [isFocused, setIsFocused] = useState(false); // Estado local para controlar o foco do carrinho
  const [itemCount, setItemCount] = useState(0);

  const calculateItemCount = () => {
    return cart.reduce((total, product) => total + (product.quantity ?? 0), 0);
  };

  useEffect(() => {
    // Atualize a contagem de itens no carrinho sempre que o carrinho for atualizado
    setItemCount(calculateItemCount());
  }, [cart]);

  const toggleCart = () => {
    setShowCart(!showCart);
  };

  const handleFocus = () => {
    setIsFocused(true);
  };

  return (
    <div className="header-container">
      <div className="logo">
        <img className="header-logo" src={Logo} alt="Logo" />
      </div>

      <div
        className={`cart ${showCart && isFocused ? "center" : ""}`}
        onClick={toggleCart}
      >
        {showCart && isFocused && (
          <div className="overlay" onClick={() => setIsFocused(false)}></div>
        )}

        <Badge
          badgeContent={itemCount}
          color="error"
          onClick={handleFocus}
          className="header-cart"
        >
          <ShoppingCartIcon sx={{ fontSize: 40 }} />
        </Badge>

        <IconButton aria-label="cart">
          <StyledBadge badgeContent={4} color="secondary">
            <ShoppingCartIcon />
          </StyledBadge>
        </IconButton>
      </div>
      <HeaderButtonGroup />
      <hr />

      {showCart && <CartComponent showCart={showCart} cart={cart} />}
    </div>
  );
}

export default Header;

