import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Products } from "../../data/products";
import { removeProduct } from "../../redux/Cart/cart-slice";
import './Cart.css';

interface CartProps {
    cart: Products[];
  }

  const CartComponent: React.FC<CartProps> = ({ cart }) => {
    const dispatch = useDispatch();

    return (
      <div className={`cart-container `}>
        <div className="cart-title">Carrinho</div>

        <div className="cart-products-list">
          {cart.map((product) => (
            <div className="cart-products-item" key={product.id}>
              <strong>{product.title}</strong> - {product.price}
              <button onClick={() => dispatch(removeProduct(product))}>
                Remover
              </button>
            </div>
          ))}
        </div>

        <div className="cart-total" data-testid="total">
          Total: ${cart.reduce((totalCart, product) => totalCart + product.price, 0)}
        </div>
      </div>
    );
  };

  export default CartComponent;
