import React, { useState } from "react";
import Logo from "../../assets/logo.png";
import Cart from "../../assets/carrinho-de-compras.svg";
import HeaderButtonGroup from "../buttonGroup/HeaderButtonGroup";
import CartComponent from "../cart/Cart";
import "./Header.css";

function Header() {
  const [showCart, setShowCart] = useState(false);
  const [cart, setCart] = useState([]);
  const [isFocused, setIsFocused] = useState(false); // Estado local para controlar o foco do carrinho

  const toggleCart = () => {
    setShowCart(!showCart);
  };

  const handleFocus = () => {
    setIsFocused(true);
  };

  return (
    <div className="header-container">
      <div className="logo">
        <img className="header-logo" src={Logo} alt="Logo" />
      </div>

      <div className={""} onClick={toggleCart}>
        <img className="header-cart" src={Cart} alt="Carrinho de Compras" onClick={handleFocus}/>
      </div>
      <HeaderButtonGroup />
      <hr />

      {showCart && <CartComponent showCart={showCart} cart={cart} />}
    </div>
  );
}

export default Header;

