import React, { useEffect, useState } from "react";
import Logo from "../../assets/img_animal/logoVet.webp";
import CartComponent from '../cart/Cart';
import "./Header.css";
import { RootReducer } from "../../redux/root-reducer";
import { useSelector } from "react-redux";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import Badge from "@mui/material/Badge";

function Header() {
  const { cart } = useSelector(
    (rootReducer: RootReducer) => rootReducer.cart
  );

  const [showCart, setShowCart] = useState(false);
  const [isFocused, setIsFocused] = useState(false); // Estado local para controlar o foco do carrinho
  const [itemCount, setItemCount] = useState(0);

  const calculateItemCount = () => {
    const count = cart.reduce((total, product) => total + (product.quantity ?? 0), 0);
    console.log("Número de itens calculado:", count);
    return count;
  };

  useEffect(() => {
    // Atualize a contagem de itens no carrinho sempre que o carrinho for atualizado
    setItemCount(calculateItemCount());
  }, [cart]

  );

  const toggleCart = () => {
    setShowCart(!showCart);
  };

  const handleFocus = () => {
    setIsFocused(true);
  };

  return (
    <div className="header-container">
      <div className="logo">
        <img className="header-logo" src={Logo} alt="Logo" /> {/*Colocar a Logo aqui*/}
        {/* <h1>Clinica Veterinária Dra. Amara Areias</h1> */}
      </div>

      <hr />

      {showCart && (
        <CartComponent
          showCart={showCart}
          cart={cart}
          onContinueShopping={() => setIsFocused(false)} // Função para remover o foco do carrinho
        />
      )}
    </div>
  );
}

export default Header;

