import * as React from 'react';
import { useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import MobileStepper from '@mui/material/MobileStepper';
import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';
import SwipeableViews from 'react-swipeable-views';
import { autoPlay } from 'react-swipeable-views-utils';

import './CarouselDepoiments.css';

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

const testimonials = [
  {
    name: 'Rogério',
    pet: 'Thor (Cachorro)',
    testimony: 'A melhor parte de poder contar com um veterinário à domicílio é que nosso pet fica à vontade. Dessa forma, não causa nenhum desconforto ou insegurança, e nosso bichinho fica sempre bem.',
  },
  {
    name: 'Ana Paula',
    pet: 'Mel (Gata)',
    testimony: 'Ter um atendimento especializado em casa fez toda a diferença. A Mel se estressa facilmente, então evitar o transporte e a clínica deixou ela muito mais tranquila.',
  },
  {
    name: 'Lucas',
    pet: 'Bob (Papagaio)',
    testimony: 'O cuidado com aves é muito específico, e o atendimento foi excelente. Saber que o veterinário entende as necessidades do Bob me deixou muito mais seguro.',
  },
  {
    name: 'Fernanda',
    pet: 'Simba (Cachorro)',
    testimony: 'Simba sempre teve medo de veterinário, mas o atendimento em casa mudou isso. O carinho e paciência da equipe fizeram toda a diferença.',
  },
  {
    name: 'Marcos',
    pet: 'Nino (Hamster)',
    testimony: 'Nunca pensei que um veterinário viria até nossa casa para cuidar de um hamster. O atendimento foi super profissional e cuidadoso, recomendo muito!',
  },
];

export default function CarouselDepoiments() {
  const theme = useTheme();
  const [activeStep, setActiveStep] = React.useState(0);
  const maxSteps = testimonials.length;

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleStepChange = (step) => {
    setActiveStep(step);
  };

  return (
    <Box 
      sx={{
        width: '100vw', // Ocupar a largura total da viewport
        flexGrow: 1,
        margin: '0 auto', // Centraliza o conteúdo horizontalmente
        textAlign: 'center', // Centraliza o conteúdo internamente
      }}
    >
      <Paper
        square
        elevation={0}
        sx={{
          display: 'flex',
          alignItems: 'center',
          height: 50,
          pl: 2,
          bgcolor: 'background.default',
        }}
      >
      </Paper>

      <AutoPlaySwipeableViews
        axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
        index={activeStep}
        onChangeIndex={handleStepChange}
        enableMouseEvents
      >
        {testimonials.map((testimonial, index) => (
          <Box
            key={index}
            sx={{     
              display: 'block',
              overflow: 'hidden',
              visibility: Math.abs(activeStep - index) <= 2 ? 'visible' : 'hidden',
              backgroundColor: '#f9f9f9',
              width: '%', // Ocupar 100% da largura disponível
              marginLeft: 'auto',
              marginRight: 'auto',
              marginBottom: '20px',
              padding: '20px',
              borderRadius: '8px',
              boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.25)',
              color: 'black',
              fontFamily: 'Poppins',
              fontWeight: 'bold',
              textAlign: 'center',
            }}
          >
            <div className="quote">&#10077;</div>
            <h3>{testimonial.name}</h3>
            <h4>{testimonial.pet}</h4>
            <p>{testimonial.testimony}</p>
          </Box>
        ))}
      </AutoPlaySwipeableViews>

      <MobileStepper
        steps={maxSteps}
        position="static"
        activeStep={activeStep}
        nextButton={
          <Button
            size="small"
            onClick={handleNext}
            disabled={activeStep === maxSteps - 1}
          >
            Next
            {theme.direction === 'rtl' ? (
              <KeyboardArrowLeft />
            ) : (
              <KeyboardArrowRight />
            )}
          </Button>
        }
        backButton={
          <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
            {theme.direction === 'rtl' ? (
              <KeyboardArrowRight />
            ) : (
              <KeyboardArrowLeft />
            )}
            Back
          </Button>
        }
      />
    </Box>
  );
}
