      <div className="cart-title">Meu Carrinho</div>
      <table className="cart-products-table">
        <thead>
          <tr>
            <th></th>
            <th></th>
            <th>Quantidade</th>
            <th>Pre칞o</th>
            <th>A칞칚o</th>
          </tr>
        </thead>
        <tbody>
          {cart.map((product) => (
            <tr key={product.id}>
              <td>
                <img src={product.image[0]} alt={product.title} />
              </td>
              <td>
                <strong>{product.title}</strong>
              </td>
              <td className="quantity-container">
                <RemoveCircleSharpIcon className="quantity-icon" />
                <span className="quantity">{product.quantity}</span>
